void DisplayInit(void){
	/*Cod nebun display*/
	Dio_WriteChannel(109, 0);
	volatile int delei = 3000000;
	while(delei){
		delei--;
	}
	Dio_WriteChannel(109, 1);
	delei = 3000000;
	while(delei){
		delei--;
	}
	host_command(CLKEXT, 0);//send command to "CLKEXT" to FT81X
	delei = 3000000;
	while(delei){
		delei--;
	}
	host_command(CLKSEL, 0x86);//select the system clock frequency
	delei = 3000000;
	while(delei){
		delei--;
	}
	/*host_command(RST_PULSE, 0);//send host command "RST_PULSE" to reset
	delei = 3000000;
	while(delei){
		delei--;
	}*/
	host_command(ACTIVE, 0);//send host command "ACTIVE" to wake up
	/*delei = 30000000;
	while(delei){
		delei--;
	}*/
	while (0x7C != rd8(REG_ID)); //Wait till clock is on
	while (0x0 != rd8(REG_CPURESET)); //Check if EVE is in working status.
	/* Configure display registers - demonstration for WQVGA resolution, modified for 800x480*/
	wr16(REG_HCYCLE, 928);
	wr16(REG_HOFFSET, 88);
	wr16(REG_HSYNC0, 0);
	wr16(REG_HSYNC1, 48);
	wr16(REG_VCYCLE, 525);
	wr16(REG_VOFFSET, 32);
	wr16(REG_VSYNC0, 0);
	wr16(REG_VSYNC1, 3);
	wr8(REG_SWIZZLE, 0);
	wr8(REG_PCLK_POL, 1);
	wr8(REG_CSPREAD, 1);
	wr16(REG_HSIZE, 800);
	wr16(REG_VSIZE, 480);
	wr8(REG_DITHER, 1);

	/* write first display list */
	wr32(RAM_DL+0,clear_color_rgb(0,0,0));
	wr32(RAM_DL+4,clear(1,1,1));
	wr32(RAM_DL+8,display());
	wr8(REG_DLSWAP,DLSWAP_FRAME);//display list swap
	wr8(REG_GPIO_DIR,0x80);//| rd8(REG_GPIO_DIR));
	wr8(REG_GPIO,0x80);// | rd8(REG_GPIO));//enable display bit
	/*wr16(REG_PWM_HZ, 1000);
	for(int i = 0; i< 128; i++){
		wr8(REG_PWM_DUTY, i);
		delei = 100000;
		while(delei){
			delei--;
		}
	}*/
	wr8(REG_PCLK,2);//after this display is visible on the LCD
}

void DisplayTest(void){
	wr32(RAM_DL + 0, clear(1, 1, 1)); // clear screen
	wr32(RAM_DL + 4, begin(BITMAPS)); // start drawing bitmaps
	wr32(RAM_DL + 8, vertex2ii(220, 110, 31, 'F')); // ascii F in font 31
	wr32(RAM_DL + 12, vertex2ii(244, 110, 31, 'T')); // ascii T
	wr32(RAM_DL + 16, vertex2ii(270, 110, 31, 'D')); // ascii D
	wr32(RAM_DL + 20, vertex2ii(299, 110, 31, 'I')); // ascii I
	wr32(RAM_DL + 24, end_d());
	wr32(RAM_DL + 28, color_rgb(160, 22, 22)); // change colour to red
	wr32(RAM_DL + 32, point_size(320)); // set point size to 20 pixels in radius
	wr32(RAM_DL + 36, begin(POINTS)); // start drawing points
	wr32(RAM_DL + 40, vertex2ii(192, 133, 0, 0)); // red point
	wr32(RAM_DL + 44, end_d());
	wr32(RAM_DL + 48, display()); // display the image
	wr8(REG_DLSWAP,DLSWAP_FRAME);//display list swap
}

