void DashboardTest(uint8 address){
	volatile uint32 delay = 35000;
	while(delay--);
	DashboardUpdate(speed, power, battery_voltage, battery_percent, battery_temp*2/3, inverter_temp/2, brake, acceleration, false, address);
	battery_percent++;
	speed++;
	power+=500;
	inverter_temp++;
	battery_voltage++;
	battery_temp++;
	brake++;
	acceleration++;
	battery_percent %= 101;
	speed %= 200;
	power %= 100000;
	inverter_temp%=202;
	battery_voltage%=150;
	battery_temp%=152;
	brake%=110;
	acceleration%=110;
}

