			sint32 x = sin(angle * 0.0175) * (POWERMETER_INNER_RADIUS + POWERMETER_RADIUS) / 2, y = cos(angle * 0.0175) * (POWERMETER_INNER_RADIUS + POWERMETER_RADIUS) / 2;
